import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';

const Home = () => {

  const onClick = () => {
    const authUrl = 'https://dev-ptqk6ibc8njgm5ty.us.auth0.com/authorize?response_type=code&client_id=xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre&state=12345&redirect_uri=http://localhost:3000/redirect&code_challenge=CR1FGKBBEJgFmdH4myjZ5izTpIdY5sskQxeJ3Nnkl6E&code_challenge_method=S256';
    // if(window.location.href.includes('code')) {
    //   return
    // }
    window.location.href = authUrl;
  }

  // useEffect(() => {
  //   const authUrl = 'https://dev-ptqk6ibc8njgm5ty.us.auth0.com/authorize?response_type=code&client_id=xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre&state=12345&redirect_uri=http://localhost:3000/redirect&code_challenge=T9WZXhjQei8x7qi83JNXPn3QOjgppKCrz1St3486oGg&code_challenge_method=S256';

  //   if(authUrl.includes('code')) return

  //   window.location.href = authUrl;
  // }, []);

  // useEffect(() => {
  //   const authUrl = 'https://dev-ptqk6ibc8njgm5ty.us.auth0.com/authorize?response_type=code&client_id=xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre&state=12345&redirect_uri=http://localhost:3000/redirect&code_challenge=CR1FGKBBEJgFmdH4myjZ5izTpIdY5sskQxeJ3Nnkl6E&code_challenge_method=S256';
  //   if(window.location.href.includes('code')) {
  //     return
  //   }
  //   window.location.href = authUrl;
  // }, []);

  return (
    <div>
      <h1>Site.</h1>
      <button onClick={onClick} title='Sign In'/>
    </div>
  );
};

const Redirect = () => {
  const location = useLocation();

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const code = query.get('code');
    const state = query.get('state');
    debugger

    if (code) {
      // Exchange the authorization code for an access token
      fetch('https://dev-ptqk6ibc8njgm5ty.us.auth0.com/oauth/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          grant_type: 'authorization_code',
          client_id: 'xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre',
          code: code,
          redirect_uri: 'http://localhost:3000/redirect',
          code_verifier: '565be7a9b4779a3c9bebba6b774ca2404d8d3398961b269d00f0bcc7',
          client_secret: 'xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre'
        })
      })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // Handle the access token
      })
      .catch(error => {
        console.error('Error:', error);
      });
    }
  }, [location]);

  return (
    <div>
      <h1>Handling Redirect...</h1>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/redirect" element={<Redirect />} />
      </Routes>
    </Router>
  );
};

export default App;
