import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';


function base64UrlEncode(str) {
  return btoa(String.fromCharCode.apply(null, new Uint8Array(str)))
    .replace(/\+/g, '-')
    .replace(/\//g, '_')
    .replace(/=+$/, '');
}

async function generateCodeChallenge(verifier) {
  const encoder = new TextEncoder();
  const data = encoder.encode(verifier);
  const digest = await window.crypto.subtle.digest('SHA-256', data);
  return base64UrlEncode(digest);
}

function generateCodeVerifier() {
  const array = new Uint32Array(43);
  window.crypto.getRandomValues(array);
  return Array.from(array, dec => ('0' + dec.toString(16)).substr(-2)).join('');
}

// Generate code verifier and code challenge
const codeVerifier = generateCodeVerifier();
generateCodeChallenge(codeVerifier).then(codeChallenge => {
  console.log('Code Verifier:', codeVerifier);
  console.log('Code Challenge:', codeChallenge);
});

const Home = () => {
  // const [codeVerifier, setCodeVerifier] = useState('');

  const onClick = async () => {
    const verifier = generateCodeVerifier();
    // setCodeVerifier(verifier);
    sessionStorage.setItem('code_verifier', verifier); 
    const challenge = await generateCodeChallenge(verifier);

    const authUrl = `https://dev-ptqk6ibc8njgm5ty.us.auth0.com/authorize?response_type=code&client_id=xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre&state=12345&redirect_uri=http://localhost:3000/redirect&code_challenge=${challenge}&code_challenge_method=S256`;
    window.location.href = authUrl;
  }


  // const onClick = () => {
  //   const authUrl = 'https://dev-ptqk6ibc8njgm5ty.us.auth0.com/authorize?response_type=code&client_id=xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre&state=12345&redirect_uri=http://localhost:3000/redirect&code_challenge=CR1FGKBBEJgFmdH4myjZ5izTpIdY5sskQxeJ3Nnkl6E&code_challenge_method=S256';
  //   // if(window.location.href.includes('code')) {
  //   //   return
  //   // }
  //   window.location.href = authUrl;
  // }


  return (
    <div>
      <h1>Site.</h1>
      <button onClick={onClick} title='Sign In'>Sign In</button>
    </div>
  );
};

const Redirect = () => {
  const location = useLocation();

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const code = query.get('code');
    const state = query.get('state');
    const codeVerifier = sessionStorage.getItem('code_verifier');
    if (code) {
      // Exchange the authorization code for an access token
      fetch('https://dev-ptqk6ibc8njgm5ty.us.auth0.com/oauth/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          grant_type: 'authorization_code',
          redirect_uri: 'http://localhost:3000/redirect',
          client_id: 'xmPoVoVk6WrffxGwPhDyOVUB3uhuDqre',
          client_secret: '0F0Kn0j_SrecdoUrzHVFgXKWA-qE4BBOxdvDbTrGGllGmVSNgmKyLGVjI7WfaWzT',
          code_verifier: codeVerifier,
          code: code,
        })
      })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // Handle the access token
      })
      .catch(error => {
        console.error('Error:', error);
      });
    }
  }, [location]);

  return (
    <div>
      <h1>Handling Redirect...</h1>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/redirect" element={<Redirect />} />
      </Routes>
    </Router>
  );
};

export default App;
